entities = [];
msg.payload.forEach(e => {
    entities.push(e.entity_id);
})
msg.entities = entities.toString();
return msg;

if (msg.reset) {
    context.set('values', []);
    return;
}

values = context.get('values');
if (msg.payload !== null && msg.payload !== undefined) {
    values.push({'ts': new Date(), 'value': Number(msg.payload)});
}
while (values.length > 0 && Math.abs(new Date() - values[0].ts) / 1000 / 60 > 5.0) {
    values.shift()
}
context.set('values', values);
msg.payload = values.reduce((i, v) => v.value + i, 0) / values.length;
if (values.length === 0) {
    msg.payload = 4.0;
}
node.status({fill: "green", text: String(msg.payload)});
return msg;

